mapper.update:
update coaching_programs
name=#{name}, introduce=#{introduce}, fee=#{fee}, coaching_type=#{coachingType}
where program_no=#{no}

mapper.select:
select cp.program_no, c.name coach_name, cp.name, cp.introduce, cp.fee from coaching_programs cp join coaches c on cp.coach_no=c.coach_no
c.gender like #{gender} and cp.coaching_type like #{coachingType}
order by program_no desc

select cp.program_no, c.name coach_name, cp.name, cp.introduce, cp.fee from coaching_programs cp join coaches c on cp.coach_no=c.coach_no join coaching_program_tags cpt on cp.program_no=cpt.program_no
tag_no=${tag}
order by program_no desc

select cp.program_no, c.name coach_name, cp.name, cp.introduce, cp.fee from coaching_programs cp join coaches c on cp.coach_no=c.coach_no join coaching_program_tags cpt on cp.program_no=cpt.program_no
tag_no=${tag}
order by program_no desc
